public class NoSafeMyRunnable implements Runnable{
    private Integer num =100;
    @Override
    public void run() {
            while (true){
                try {
                    Thread.sleep(5);
                } catch (InterruptedException e) {
                    e.printStackTrace();
                }
                synchronized (NoSafeRunnable.class){
                    if(num > 0){
                    System.out.println(Thread.currentThread().getName()+"买票:"+num);
                    num--;
                }else{
                    break;
                }
            }
        }
    }
}

